Events.GetGlobalAchievements:
(block (theList)
	(if (msnFind "r +unid:&HS_msCSCRescueScientists;; +property:isSuccess;")
		(setq theList (list
			(list 
				"Rescued Project Lamplighter scientists"
				Nil
				"achievements & regrets"
				)
			))
		)
	(if (msnFind "r +unid:&HS_msCSCRescueScientists;; +property:isFailure;")
		(setq theList (list
			(list 
				"Failed to rescue Project Lamplighter scientists"
				Nil
				"achievements & regrets"
				)
			))
		)

	theList
	)
