Events.OnDestroyShot:
(block (
	(asteroidsFound (objGetProperty gSource 'mining.asteroidsFound))
	)
	(switch
		(= asteroidsFound 0)
			(objSendMessage aOrderGiver Nil (typTranslate &GLXY_ovMiningOreCount; 'msgNoOreFound))

		(= asteroidsFound 1)
			(objSendMessage aOrderGiver Nil (typTranslate &GLXY_ovMiningOreCount; 'msgOreFound1))

		(objSendMessage aOrderGiver Nil (typTranslate &GLXY_ovMiningOreCount; 'msgOreFoundN {
			count: asteroidsFound
			}))
		)
	)
